import { useEffect, useState } from "react";
import {Navbar, Container, Nav } from "react-bootstrap";
import logo from "../assets/img/logo-black.png";
import {VscGithub} from "react-icons/vsc";
import {FaLinkedin} from "react-icons/fa";
import {
  BrowserRouter as Router
} from "react-router-dom";



export const NavBar = () => {

  const [activeLink, setActiveLink] = useState('home');
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    }

    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, [])

  const onUpdateActiveLink = (value) => {
    setActiveLink(value);
  }
  return (
    <Router>
    <Navbar expand="md" className={scrolled ? "scrolled" : ""}>
    <Container>
      <Navbar.Brand href="/">
        <img src={logo} alt="Logo" />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav">
        <span className="navbar-toggler-icon"></span>
      </Navbar.Toggle>
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="ms-auto">
          <Nav.Link href="#home" className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('home')}>Home</Nav.Link>
          <Nav.Link href="#sobre" className={activeLink === "sobre" ? "active navbar-link" : "navbar-link"} onClick={onUpdateActiveLink("sobre")}>Sobre</Nav.Link>
            <Nav.Link href="#projetos" className={activeLink === "projetos" ? "active navbar-link" : "navbar-link"} onClick={onUpdateActiveLink("projetos")}>Projetos</Nav.Link>
            <Nav.Link href="#contato" className={activeLink === "contato" ? "active navbar-link" : "navbar-link"} onClick={onUpdateActiveLink("contato")}>Contato</Nav.Link>
        </Nav>
        <span className="navbar-text">
          <div className="social-icon">
            <a href="https://github.com/DayaneAlRodrigues">< VscGithub /></a>
            <a href="https://www.linkedin.com/in/dayane-de-almeida-rodrigues-067063219/"><FaLinkedin/></a>
          </div>
          <button className="cv" onClick={() => console.log("click")}>
              <span>
                Baixe meu Curriculo!
              </span>
          </button>
        </span>
      </Navbar.Collapse>
    </Container>
  </Navbar>
  </Router>
  );
}
